def all_ways(k, n, cities):    for i in range(len(array_cities[k-1])):        if array_cities[k-1][i] == '1':            cities.append(i+1)            if n <= number_of_transfers-2:                cities = all_ways(i,n+1,cities)    return citieswith open('2.txt', "r", encoding='utf-8') as f:    val_cities = int(f.readline())    array_cities = []    for i in range(val_cities):        array_cities.append(f.readline().rstrip())initial_city = int(input('Введите начальный город: '))number_of_transfers = int(input('Введите максимальное кол-во пересадок: '))cities = []cities = all_ways(initial_city, 0, cities)if cities is []:    print(-1)else:    cities_dop = []    for i in cities:        if i not in cities_dop:            cities_dop.append(i)    line = '\nОтвет: '    for i in sorted(cities_dop):        line += f'{i}, '    print(line[:-2])